for(var i=0;i<document.querySelectorAll(".drum").length;i++)document.querySelectorAll(".drum")[i].addEventListener("click",function(){var e=this.innerHTML;makeAsound(e),addAnimation(e)});var keypress=document.addEventListener("keydown",function(e){key=e.key,makeAsound(key),addAnimation(key)});function makeAsound(e){switch(e){case"a":new Audio("sounds/tom-1.mp3").play();break;case"s":new Audio("sounds/tom-2.mp3").play();break;case"d":new Audio("sounds/tom-3.mp3").play();break;case"f":new Audio("sounds/tom-4.mp3").play();break;case"j":new Audio("sounds/snare.mp3").play();break;case"k":new Audio("sounds/crash.mp3").play();break;case"l":new Audio("sounds/kick-bass.mp3").play()}}function addAnimation(e){document.querySelector("."+e).classList.add("pressed"),setTimeout(function(){document.querySelector("."+e).classList.remove("pressed")},100)}
